(41, 1, '2023-05-17 20:42:21', '2023-05-17 20:42:21', '<!-- wp:cover {\"url\":\"http://localhost/wordpress/wp-content/uploads/2023/05/glasses-1024x768.jpg\",\"id\":76,\"dimRatio\":50,\"style\":{\"color\":{}}} -->\n<div class=\"wp-block-cover\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-76\" alt=\"Master\" src=\"http://localhost/wordpress/wp-content/uploads/2023/05/glasses-1024x768.jpg\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n    <div class=\"text-white text-center div-position fs-4\">\n        <p class=\"mt-3\">\n            Recherchez dans notre base de données des programmes de maîtrise, <br>\n            y compris des programmes à temps partiel, à distance, <br>\n            MA, MSc, MBA, MRes et MPhil,<br>\n            ainsi que d\'autres opportunités d\'études de troisième cycle.<br><br>\n        </p>\n        <form class=\"d-flex justify-content-center\" role=\"search\">\n            <a href=\"university_list/\" target=\"_blank\" rel=\"noopener noreferrer\" class=\"btn btn-success me-4\">Liste des universités</a>\n            <a href=\"masters_list/\" target=\"_blank\" rel=\"noopener noreferrer\" class=\"btn btn-success\">Liste des masters</a>\n        </form>\n    </div>\n<!-- /wp:html --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":3,\"className\":\"mt-4\"} -->\n<h3 class=\"wp-block-heading has-text-align-center mt-4\">Catégories de recherche populaires</h3>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<div class=\"mt-4 container\">\n    <div class=\"row justify-content-center mb-5\">\n        <div class=\"col-md-3 mt-4\">\n            <div class=\"card mt-4 h-100\">\n                <div class=\"card-body\">\n                    <h5 class=\"text-center\">\n                        <i class=\"fa-solid fa-calcu